import React from 'react';
import { Link, animateScroll as scroll } from 'react-scroll'; // Import animateScroll as scroll
import '../src/css/navbar.css'; // Import the CSS file

const Navbar = () => {
  const tabs = [
    { path: 'home', label: 'Home' },
    { path: 'page', label: 'Pages' },
    { path: 'tab3', label: 'Portfolio' },
    { path: 'tab4', label: 'Services' },
    { path: 'tab5', label: 'Blog' },
    { path: 'tab6', label: 'Contact Us' },
    // Add more tabs as needed
  ];

  // Scroll to the top of the page when the logo is clicked
  const scrollToTop = () => {
    scroll.scrollToTop();
  };

  return (
    <nav>
      <div className="logo" onClick={scrollToTop}> {/* Add onClick event */}
        <img src="/img/logo.png" alt="Logo" />
      </div>
      <ul className="nav-links">
        {tabs.map((tab) => (
          <li key={tab.path}>
            <Link
              to={tab.path}
              smooth={true}
              duration={500}
            >
              {tab.label}
            </Link>
          </li>
        ))}
      </ul>
      <div className="address">
        <p>123 Main Street, City, Country</p>
      </div>
      <div className="search">
        <input type="text" placeholder="Search..." />
        <button>Search</button>
      </div>
    </nav>
  );
};

export default Navbar;
